import React, {useState, useEffect} from 'react';
import Header from '../../componentes/Header';
import Footer from '../../componentes/Footer';
import { Card, CardContent, Container, Typography, Button, Grid, Box } from '@mui/material';

function AdmResenias() {

    const [res, setRes] = useState([])

    const cargarResenias = async () => {
        const response = await fetch ('http://localhost:4000/Resenias')
        const data =  await response.json ()
        setRes(data)
    }
    console.log(res)

    useEffect(() => {
        cargarResenias()
    }, [])

    return (
        <>
            <div>
                <Header/>
                <div>
                    {
                        res.map((resenias, nombreusuario, id_resenia, isbn) => (
                            <Card style = {{
                                marginBottom: ".7rem",
                                backgroundColor:"rgb(255, 255, 255)",
                                width: "95%",
                                marginLeft: "auto",
                                marginRight: "auto"
                            }}>
                                <CardContent style={{
                                    display: "flex",
                                    justifyContent: "space-between"
                                }}>

                                    <div>
                                        <Typography>{resenias.resenias}</Typography>
                                        <Typography>{resenias.nombreusuario}</Typography>
                                        <Typography>{resenias.id_resenia}</Typography>
                                        <Typography>{resenias.isbn}</Typography>
                                    </div>

                                    <div>
                                        <Button variant='contained' color='warning'>
                                        Eliminar
                                        </Button>
                                    </div>

                                    

                                </CardContent>
                            </Card>
                        ))
                    }
                </div>
                <Grid container>
                    <Grid item xs={6}>
                        <Box border={2}>
                            algo por aca
                        </Box>
                    </Grid>

                    <Grid item xs={6}>
                        <Box border={2}>
                            algo por aca
                        </Box>
                    </Grid>
                    <Grid item xs={6}>
                        <Box border={2}>
                            algo por aca
                        </Box>
                    </Grid>
                    <Grid item xs={6}>
                        <Box border={2}>
                            algo por aca
                        </Box>
                    </Grid>
                </Grid>
            </div>
           
        </>
    )
}

export default AdmResenias;
