import React, {useState, useEffect} from 'react';
import Header from '../../componentes/Header';
import Footer from '../../componentes/Footer';
import { Card, CardContent, Container, Typography, Button, Grid, Box } from '@mui/material';
import '../../hojas-de-estilo/AdmResenias.css';

function AdmResenias() {

    const [res, setRes] = useState([])

    const cargarResenias = async () => {
        const response = await fetch ('http://localhost:4000/Resenias')
        const data =  await response.json ()
        setRes(data)
    }
    console.log(res)

    useEffect(() => {
        cargarResenias()
    }, [])

    return (
        <>
            <div>
                <Header/>
                <div style={{marginLeft:"20px", marginRight:"auto"}}>
                    {
                        res.map((resenia) => (
                            <Grid container style={{backgroundColor:"rgb(255, 255, 255)"}}>

                                <Grid item xs={5} >
                                    <Box border={1} sx={{display: "flex"}}>
                                        <Typography>{resenia.resenias}</Typography>
                                    </Box>
                                </Grid>
                                <Grid item xs={2} left={"20px"}>
                                    <Box border={1} >
                                        <Typography>{resenia.nombreusuario}</Typography>
                                    </Box>
                                </Grid>
                                <Grid item xs={2}>
                                    <Box border={1}>
                                        <Typography>{resenia.isbn}</Typography>
                                    </Box>
                                </Grid>
                                <Grid item xs={2}>
                                    <Box border={1}>
                                        <Typography>{resenia.id_resenia}</Typography>
                                    </Box>
                                </Grid>

                                <Button color='warning' variant='contained'>
                                    
                                </Button>

                            </Grid>
                        ))
                    }
                </div>
                
            </div>
           
        </>
    )
}

export default AdmResenias;
