import React from 'react';
import { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Button } from '@mui/material';

function prueba() {

    const [contador, setContador] = useState(0)

    console.log(contador)

    const suma = async (contar) => {
        setContador
        console.log('esta sumando')
        console.log(cont)
    }
    
    const resta = async (contar) => {
        cont = contar - 1;
        console.log('esta restando')
        console.log(cont)
    }
    

    return(
        <>
            <div>
                <h1>{cont}</h1>
                <Button onClick={() => suma(cont)} sx={{backgroundColor: "red", color: "white"}}> Sumar</Button>
                <Button onClick={() => resta(cont)} sx={{backgroundColor: "black", color: "white"}}> Restar</Button>
            </div>
        </>
    )
}

export default prueba;
